insert into sfg_forms set `status`='1',`name`='Contact Us',`description`='Sample form. Allows collecting visitors\' requests from the external part of the site. Features:\n* Conditional branching. Displaying states when US is selected\n* Expanding the form when a new field is added\n* Protected with Captcha\n* Saving the time of submission',`form`='<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<form name=\"Contact Us\" ext_access=\"111\">\n  <description>\n    Sample form. Allows collecting visitors\' requests from the external part of the site. Features:\n* Conditional branching. Displaying states when US is selected\n* Expanding the form when a new field is added\n* Protected with Captcha\n* Saving the time of submission\n  </description>\n  <database name=\"sfg_example_contact\">\n    <map sfgfield=\"\" dbfield=\"id\" />\n    <map sfgfield=\"name\" dbfield=\"name\" />\n    <map sfgfield=\"email\" dbfield=\"email\" />\n    <map sfgfield=\"message\" dbfield=\"message\" />\n    <map sfgfield=\"file\" dbfield=\"file\" />\n    <map sfgfield=\"country\" dbfield=\"country\" />\n    <map sfgfield=\"company\" dbfield=\"company\" />\n    <map sfgfield=\"phone\" dbfield=\"phone\" />\n    <map sfgfield=\"state\" dbfield=\"state\" />\n    <map sfgfield=\"date_time\" dbfield=\"submission_time\" />\n  </database>\n  <globalphp>\n    /*******************************************************************/\n/* SmartFormer Gold (c) Form PHP controller                                                     */\n/*                                                                                                                  */\n/* Product of IToris (c) 2009 http://www.itoris.com                                       */\n/*                                                                                                                  */\n/*******************************************************************/\n\n$form-&gt;getPostedValues();\n\n$form-&gt;data[\'date_time\'] = date(\'r\');\n\n$form-&gt;doAfterSubmitAction();\n\n$form-&gt;renderForm();\n\necho $form-&gt;page_html;\n  </globalphp>\n  <globaljs>\n    /*******************************************************************/\n// SmartFormer Gold (c) Form JS controller\n//\n// Product of IToris (c) 2009 http://www.itoris.com\n//\n/*******************************************************************/\n\nfunction formSubmit(submitter) {\n	//getting list of elements\n	var nodes = document.getElementById(\'sfg_fieldset\').childNodes;\n	var objects = Array();\n	for (var i=0; i&lt;nodes.length; i++) if (nodes[i].tagName) objects[objects.length] = nodes[i];\n	// perform validation\n	var isFormValid = true;\n	for (var vl=0; vl&lt;sfg_validation.length; vl++) if (window[\'validator\'+sfg_validation[vl][1]] &amp;&amp; objects[sfg_validation[vl][0]]) {\n		var msg = window[\'validator\'+sfg_validation[vl][1]](objects[sfg_validation[vl][0]], sfg_validation[vl][2], sfg_validation[vl][3] ? sfg_validation[vl][3] : \'\');\n		if (msg) {\n			isFormValid = false;\n			alert(msg);\n			objects[sfg_validation[vl][0]].focus();\n			return;\n		}\n	}\n	document.getElementById(\'sfg_submitter\').value = submitter;\n	document.sfgForm.submit();\n}\n\nvar objs = Array();\nvar isUS = true;\n\nfunction countrySelected(obj) {\n	var stateField = document.getElementsByName(\'state\')[0];\n	var stateSpan = document.getElementById(\'state_span\');\n	if (obj.value.toLowerCase()==\'united states\' &amp;&amp; !isUS) {\n		isUS = true;\n		stateField.style.display = \'block\';\n		stateSpan.style.display = \'block\';\n		moveElementsDown(objs, 28);\n	} else if (obj.value.toLowerCase()!=\'united states\' &amp;&amp; isUS) {\n		isUS = false;\n		stateField.selectedIndex = 0;\n		objs = moveElementsUp(stateField, 28);\n		stateField.style.display = \'none\';	\n		stateSpan.style.display = \'none\';	\n	}\n}\n\nfunction onFormLoad() {\n	if (!document.getElementsByName(\'country\')[0]) {\n		setTimeout(\'onFormLoad()\', 20);\n		return;\n	}\n	if (document.getElementsByName(\'country\')[0].value.toLowerCase()!=\'united states\') {\n		countrySelected(document.getElementsByName(\'country\')[0]);\n	}\n}\n\nonFormLoad();\n  </globaljs>\n  <globalhtml>\n    &lt;!-- SmartFormer Gold code part --&gt;\n\n&lt;form action=\"\" method=\"post\" name=\"sfgForm\" enctype=\"multipart/form-data\"&gt;\n&lt;%form_html%&gt;\n&lt;input type=\"hidden\" name=\"date_time\" /&gt;\n&lt;/form&gt;\n\n&lt;!-- End of SmartFormer Gold code part --&gt;\n  </globalhtml>\n  <globalcss>\n    #sfg_err_message {\n	font-family: Arial;\n	font-size: 12px;\n	font-weight: bold;\n	color: #ff0000;\n}\n  </globalcss>\n  <page name=\"Page 1\" id=\"0\" />\n  <page name=\"Page 2\" id=\"1\" />\n  <page name=\"Page 3\" id=\"2\" />\n  <page name=\"Page 4\" id=\"3\" />\n  <page name=\"Page 5\" id=\"4\" />\n  <page name=\"Page 6\" id=\"5\" />\n  <page name=\"Page 7\" id=\"6\" />\n  <page name=\"Page 8\" id=\"7\" />\n  <page name=\"Page 9\" id=\"8\" />\n  <page name=\"Page 10\" id=\"9\" />\n  <element sfgalias=\"\" tag=\"span\" page=\"0\">\n    <content>\n      Name:\n    </content>\n    <style name=\"left\" value=\"97px\" />\n    <style name=\"top\" value=\"60px\" />\n    <style name=\"font-weight\" value=\"bold\" />\n  </element>\n  <element sfgalias=\"Name\" tag=\"input\" page=\"0\">\n    <attribute name=\"type\" value=\"text\" />\n    <attribute name=\"name\" value=\"name\" />\n    <style name=\"width\" value=\"262px\" />\n    <style name=\"height\" value=\"16px\" />\n    <style name=\"left\" value=\"145px\" />\n    <style name=\"top\" value=\"58px\" />\n    <event name=\"onblur\" value=\"\" />\n    <event name=\"onresize\" value=\"\" />\n    <event name=\"onchange\" value=\"\" />\n    <event name=\"onactivate\" value=\"\" />\n    <event name=\"onafterupdate\" value=\"\" />\n  </element>\n  <element sfgalias=\"\" tag=\"span\" page=\"0\">\n    <content>\n      Email:&lt;span style=\"color:red\"&gt;*&lt;/span&gt;\n    </content>\n    <style name=\"left\" value=\"94px\" 