)&amp;&amp;obj.value.length&gt;0) return msg;\n    </js>\n    <php>\n      // incoming variables: $form - the form object, $obj - structured object of element firing the validation\n$msg = \'Field: \'.$obj[\'sfgalias\'].\'. Invalid US Zip. Please specify a valid value\';\n$htmlName = str_replace(array(\'[\', \']\', \' \'), array(\'\',\'\',\'\'), $form-&gt;getProp($obj[\'attributes\'], \'name\'));\n$htmlName = strtolower($htmlName);\n$zipPattern1=\"/^(\\d{5})$/\";\n$zipPattern2=\"/^(\\d{5})\\-(\\d{4})$/\";\nif (isset($form-&gt;data[$htmlName]) &amp;&amp; $form-&gt;data[$htmlName]!=\'\' &amp;&amp; !is_array($form-&gt;data[$htmlName]) &amp;&amp; !preg_match($zipPattern1, $form-&gt;data[$htmlName]) &amp;&amp; !preg_match($zipPattern2, $form-&gt;data[$htmlName])) return $msg;\n    </php>\n  </validator>\n  <validator name=\"Phone\">\n    <js>\n      // incoming variables: obj - HTML element firing the event, sfgName - system name of the object \nvar msg = \'Field: %s. Please specify a correct phone number\';\nmsg = msg.replace(\'%s\', sfgName);\nvar letters=\'1234567890 -.()+\';\nvar ps=false;\nfor(i=0;i&lt;obj.value.length;i++) if (letters.indexOf(obj.value.toLowerCase().substr(i,1))&lt;0) ps=true;\nif (ps || obj.value.length&gt;10) return msg;\n    </js>\n    <php>\n      // incoming variables: $form - the form object, $obj - structured object of element firing the validation\n$msg = \'Field: \'.$obj[\'sfgalias\'].\'. Please specify a correct phone number\';\n$htmlName = str_replace(array(\'[\', \']\', \' \'), array(\'\',\'\',\'\'), $form-&gt;getProp($obj[\'attributes\'], \'name\'));\n$htmlName = strtolower($htmlName);\n$letters = \'1234567890 -.()+\';\nif (isset($form-&gt;data[$htmlName])) $value = strtolower($form-&gt;data[$htmlName]); else $value=\'\';\nfor($i=0; $i&lt;strlen($value); $i++) if (strpos($letters, substr($value, $i, 1))===false) return $msg;\n    </php>\n  </validator>\n  <validator name=\"PhoneUS\">\n    <js>\n      // incoming variables: obj - HTML element firing the event, sfgName - system name of the object \nvar msg = \'Field: %s. Invalid value! Please, provide a phone number in format XXX-XXX-XXXX, where X is a digit.\';\nmsg = msg.replace(\'%s\', sfgName);\nvar phonePattern=/^(\\d{3})\\-(\\d{3})\\-(\\d{4})$/;\nif ((obj.value.match(phonePattern)==null)&amp;&amp;obj.value.length&gt;0) return msg;\n    </js>\n    <php>\n      // incoming variables: $form - the form object, $obj - structured object of element firing the validation\n$msg = \'Field: \'.$obj[\'sfgalias\'].\'. Invalid value! Please, provide a phone number in format XXX-XXX-XXXX, where X is a digit.\';\n$htmlName = str_replace(array(\'[\', \']\', \' \'), array(\'\',\'\',\'\'), $form-&gt;getProp($obj[\'attributes\'], \'name\'));\n$htmlName = strtolower($htmlName);\n$phonePattern=\"/^(\\d{3})\\-(\\d{3})\\-(\\d{4})$/\";\nif (isset($form-&gt;data[$htmlName]) &amp;&amp; $form-&gt;data[$htmlName]!=\'\' &amp;&amp; !is_array($form-&gt;data[$htmlName]) &amp;&amp; !preg_match($phonePattern, strtolower($form-&gt;data[$htmlName]))) return $msg;\n    </php>\n  </validator>\n  <validator name=\"Password\">\n    <js>\n      // incoming variables: obj - HTML element firing the event, sfgName - system name of the object \nvar msg = \'Field: %s. The password should contain 6 to 12 symbols\';\nmsg = msg.replace(\'%s\', sfgName);\nif ((obj.value.length&lt;6 || obj.value.length&gt;12) &amp;&amp; obj.value.length!=0) return msg;\n    </js>\n    <php>\n      // incoming variables: $form - the form object, $obj - structured object of element firing the validation\n$msg = \'Field: \'.$obj[\'sfgalias\'].\'. The password should contain 6 to 12 symbols\';\n$htmlName = str_replace(array(\'[\', \']\', \' \'), array(\'\',\'\',\'\'), $form-&gt;getProp($obj[\'attributes\'], \'name\'));\n$htmlName = strtolower($htmlName);\nif (isset($form-&gt;data[$htmlName]) &amp;&amp; $form-&gt;data[$htmlName]!=\'\' &amp;&amp; !is_array($form-&gt;data[$htmlName]) &amp;&amp; (strlen($form-&gt;data[$htmlName])&lt;6 || strlen($form-&gt;data[$htmlName])&gt;12)) return $msg;\n    </php>\n  </validator>\n  <validator name=\"CreditCardNumber\">\n    <js>\n      // incoming variables: obj - HTML element firing the event, sfgName - system name of the object \nvar msg = \'Field: %s. Please specify a correct credit card number\';\nmsg = msg.replace(\'%s\', sfgName);\nvar ccPattern=/^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|6(?:011|5[0-9][0-9])[0-9]{12}|3[47][0-9]{13}|3(?:0[0-5]|[68][0-9])[0-9]{11}|(?:2131|1800|35\\d{3})\\d{11})$/;\nif ((obj.value.match(ccPattern)==null)&amp;&amp;obj.value.length&gt;0) return msg;\n    </js>\n    <php>\n      // incoming variables: $form - the form object, $obj - structured object of element firing the validation\n$msg = \'Field: \'.$obj[\'sfgalias\'].\'. Please specify a correct credit card number\';\n$htmlName = str_replace(array(\'[\', \']\', \' \'), array(\'\',\'\',\'\'), $form-&gt;getProp($obj[\'attributes\'], \'name\'));\n$htmlName = strtolower($htmlName);\n$ccPattern=\"/^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|6(?:011|5[0-9][0-9])[0-9]{12}|3[47][0-9]{13}|3(?:0[0-5]|[68][0-9])[0-9]{11}|(?:2131|1800|35\\d{3})\\d{11})$/\";\nif (isset($form-&gt;data[$htmlName]) &amp;&amp; $form-&gt;data[$htmlName]!=\'\' &amp;&amp; !is_array($form-&gt;data[$htmlName]) &amp;&amp; !preg_match($ccPattern, strtolower($form-&gt;data[$htmlName]))) return $msg;\n    </php>\n  </validator>\n  <validator name=\"CreditCardCVV\">\n    <js>\n      // incoming variables: obj - HTML element firing the event, sfgName - system name of the object \nvar msg = \'Field: %s. Please specify a correct credit card CVV\';\nmsg = msg.replace(\'%s\', sfgName);\nvar letters=\'1234567890\';\nvar ps=false;\nfor(i=0;i&lt;obj.value.length;i++) if (letters.indexOf(obj.value.toLowerCase().substr(i,1))&lt;0) ps=true;\nif (ps || (obj.value.length&lt;3 &amp;&amp; obj.value.length!=0) || obj.value.length&gt;4) return msg;\n    </js>\n    <php>\n      // incoming variables: $form - t